import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const Update = () => {
    const { id } = useParams(); 
    const [ticketData, setTicketData] = useState({
        eventName: '',
        eventDate: '',
        eventPrice: '',
        quantity: ''
    });

    useEffect(() => {
        // Fetch ticket data based on id
        fetch('http://localhost:8100/web/tickets/' + id)
            .then(response => response.json())
            .then(data => {
                setTicketData(data); // Update state with fetched ticket data
            })
            .catch(error => console.error('Error fetching ticket:', error));
    }, [id]);

    const handleSubmit = (e) => {
        e.preventDefault();
        const ticket = { ...ticketData }; // Use updated ticketData state

        fetch('http://localhost:8100/web/tickets/' + id,  {
            method: 'PUT',
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(ticket)
        }).then(() => {
            console.log('Ticket updated');
            window.location.href = '/'; 
        });
    }

    const handleChange = (e) => {
        const { name, value } = e.target;
        setTicketData(prevState => ({
            ...prevState,
            [name]: value
        }));
    }

    return(
        <div className="create">
            <h2> Update ticket</h2>
            <form onSubmit={handleSubmit}>
    
                <label> Event name:</label>
                <input type="text" name="eventName" required value={ticketData.eventName} onChange={handleChange}>
                </input>
    
                <label> Event date:</label>
                <input type="text" name="eventDate" required value={ticketData.eventDate} onChange={handleChange}>
                </input>
    
                <label> Event price:</label>
                <input type="text" name="eventPrice" required value={ticketData.eventPrice} onChange={handleChange}>
                </input>

                <label> Quantity</label>
                <input type="text" name="quantity" required value={ticketData.quantity} onChange={handleChange}>
                </input>
    
                <button type="submit">Update Ticket</button>
    
            </form>
        </div>
    )
}

export default Update;
