import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import UserList from './UserList';
import useFetch from "./useFetch"; // Import the useFetch hook

const Home = () => {
  const { error, isPending, data: users } = useFetch('http://localhost:8100/web/users');

  return (
    <div className="home">
      {error && <div>{error}</div>}
      {isPending && <div>Loading...</div>}
      {users && <UserList data={users} />} {/* Pass data to UserList */}
    </div>
  );
}

export default Home;
