import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const UserList = ({ data }) => { // Receive data as a prop
    const [users, setUsers] = useState(data); // Initialize state with data
  
    useEffect(() => {
      setUsers(data); // Update state when data changes
    }, [data]); // Add data as a dependency
  
    return (
      <div>
        {users.map(user => (
          <div className="user-preview" key={user.id}>
            <Link to={`/users/${user.id}`}>
              <p>First name: {user.firstName}</p> {/* Correct typo: Fist -> First */}
              <p>Last name: {user.lastName}</p>
              <p>Email: {user.email}</p>
            </Link>
          </div>
        ))}
      </div>
    );
  };
  
  export default UserList;
