import React from "react";
import "./App.css";
import Create from './CreateUser'
import axios from "axios"; 
class App extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			items: [],
			DataisLoaded: false,
		};
	}


	componentDidMount() {
		axios.get(
				"http://localhost:8100/web/users"
			)
			.then((res) => {
				this.setState({
					items: res.data,
					DataisLoaded: true,
				});
			});
	}
	render() {
		const { DataisLoaded, items } = this.state;
		if (!DataisLoaded)
			return (
				<div>
					<h1> Pleses wait some time.... </h1>
				</div>
			);

		return (
			<div className="App">
				<h1>Users</h1>
				<div className="container">
					{items.map((item) => (
						<div className="item">
							<ol key={item.id}>
								<div>
									FirstName: {item.firstName},
								</div>
								<div>
									LastName: {item.lastName}
								</div>
                <div>
									Email: {item.email}
								</div>
							</ol>
						</div>
					))}
				</div>
			</div>
		);
	}
}

export default App;
